import React from 'react';
import { Chart } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    LinearScale,
    CategoryScale,
    BarElement,
    PointElement,
    LineElement,
    Legend,
    Tooltip,
} from 'chart.js';

const ReadTimeChart = ({
    endDate = new Date(),
    startDate = endDate.startDate(endDate - 14),
    timeData,
}) => {
    ChartJS.register(
        LinearScale,
        CategoryScale,
        BarElement,
        PointElement,
        LineElement,
        Legend,
        Tooltip
    );

    const startEndDateDifference = Math.ceil(
        Math.abs(endDate - startDate) / (1000 * 60 * 60 * 24)
    );
    const startDateDay = startDate.getDate();

    return (
        <Chart
            type='line'
            data={{
                labels: [
                    ...Array.from(
                        { length: startEndDateDifference },
                        (_, i) => {
                            const day = startDate.setDate(startDateDay + i);
                            return day.getDate() + '일';
                        }
                    ),
                ],
                datasets: [
                    {
                        label: '',
                        backgroundColor: 'rgb(255, 99, 132)',
                        borderColor: 'rgb(255, 99, 132)',
                        data: [
                            ...Array.from(
                                { length: startEndDateDifference },
                                (_, i) => Math.random() * 250
                            ),
                        ],
                    },
                ],
            }}
        />
    );
};

export default ReadTimeChart;
