import React from 'react';
import { Card, Container } from 'react-bootstrap';
import koreaIcon from '../images/language/korea.png';
import novelIcon from '../images/category/novel.png';
import ebookIcon from '../images/medium/ebook.png';
import timeIcon from '../images/statistics/time.png';
import bookIcon from '../images/statistics/book.png';
import averageIcon from '../images/statistics/average.png';
// Components
import HorizontalBookView from '../common/HorizontalBookView';
import DateLineChart from '../statistics/DateLineChart';

const Main = () => {
    const dummy = {
        lastBook: {
            title: '위대한 게츠비',
            author: 'F. 스콧 피츠제럴드',
            image: 'http://minumsa.minumsa.com/wp-content/uploads/bookcover/075_위대한개츠비-300x511.jpg',
            startDate: '',
            currentPage: 100,
            endPage: 400,
            language: {
                name: '한국어',
                image: koreaIcon,
            },
            category: {
                name: '소설',
                image: novelIcon,
            },
            medium: {
                name: '이북',
                image: ebookIcon,
            },
        },
        statistics: [
            {
                id: 1,
                icon: timeIcon,
                name: '총 독서시간',
                value: '201시간',
            },
            {
                id: 3,
                icon: averageIcon,
                name: '하루 평균 독서시간',
                value: '1시간 12분',
            },
            {
                id: 2,
                icon: bookIcon,
                name: '읽은 책',
                value: '66권',
            },
        ],
    };

    return (
        <Container>
            <div className='row row-eq-height'>
                <div className='col-lg-12 col-xl-6 mb-4'>
                    <Card className='mb-4 h-100'>
                        <Card.Body>
                            <h3>마지막으로 읽은 책</h3>

                            <HorizontalBookView book={dummy.lastBook} />
                        </Card.Body>
                    </Card>
                </div>

                <div className='col-lg-12 col-xl-6 mb-4'>
                    <Card className='h-100'>
                        <Card.Body>
                            <h3>최근 2주간 독서시간</h3>

                            <DateLineChart
                                startDate={new Date().setDate(Date.now() - 14)}
                                data={[
                                    ...Array.from(
                                        { length: 14 },
                                        (_, i) => Math.random() * 250
                                    ),
                                ]}
                            />
                        </Card.Body>
                    </Card>
                </div>

                <div className='col-lg-12 col-xl-6 mb-4'>
                    <Card className='h-100'>
                        <Card.Body>
                            <h3>목표</h3>
                        </Card.Body>
                    </Card>
                </div>
                <div className='col-lg-12 col-xl-6 mb-4'>
                    <Card className='h-100'>
                        <Card.Body>
                            <h3>2022년 독서 요약</h3>

                            <table class='table table-hover mt-3'>
                                <tbody>
                                    {dummy.statistics.map((stat) => {
                                        return (
                                            <tr>
                                                <th className='col-8 h4'>
                                                    <img
                                                        src={stat.icon}
                                                        alt=''
                                                        className='img-fluid me-3'
                                                        style={{
                                                            width: '25px',
                                                            height: '25px',
                                                        }}
                                                    />
                                                    {stat.name}
                                                </th>
                                                <td className='col-4'>
                                                    {stat.value}
                                                </td>
                                            </tr>
                                        );
                                    })}
                                </tbody>
                            </table>
                        </Card.Body>
                    </Card>
                </div>
            </div>
        </Container>
    );
};

export default Main;
