import React from 'react';
import { Chart } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    LinearScale,
    CategoryScale,
    BarElement,
    PointElement,
    LineElement,
    Legend,
    Tooltip,
} from 'chart.js';

const ReadTimeChart = ({
    endDate = new Date(),
    startDate = new Date(endDate).setDate(endDate.getDate - 14),
    timeData,
}) => {
    ChartJS.register(
        LinearScale,
        CategoryScale,
        BarElement,
        PointElement,
        LineElement,
        Legend,
        Tooltip
    );

    return (
        <Chart
            type='line'
            data={{
                labels: [
                    ...Array.from(
                        {
                            length: 14,
                        },
                        (_, i) => {
                            const date = startDate.getDate();
                            const day = new Date(startDate).setDate(date + i);
                            return day.getDate() + '일';
                        }
                    ),
                ],

                datasets: [
                    {
                        label: '',
                        backgroundColor: 'rgb(255, 99, 132)',
                        borderColor: 'rgb(255, 99, 132)',
                        data: [
                            ...Array.from(
                                {
                                    length:
                                        typeof endDate == 'object' &&
                                        typeof startDate == 'object' &&
                                        endDate.getTime() -
                                            startDate.getTime() /
                                                (1000 * 60 * 60 * 24),
                                },
                                (_, i) => Math.random() * 250
                            ),
                        ],
                    },
                ],
            }}
        />
    );
};

export default ReadTimeChart;
