import React from 'react';
import { Card, Container } from 'react-bootstrap';
import koreaIcon from '../images/language/korea.png';
import novelIcon from '../images/category/novel.png';
import ebookIcon from '../images/medium/ebook.png';
import HorizontalBookCard from '../common/HorizontalBookCard';
import { Chart } from 'react-chartjs-2';

const Main = () => {
    const dummy = {
        lastBook: {
            title: '위대한 게츠비',
            author: 'F. 스콧 피츠제럴드',
            image: 'http://minumsa.minumsa.com/wp-content/uploads/bookcover/075_위대한개츠비-300x511.jpg',
            startDate: '',
            currentPage: 100,
            endPage: 400,
            language: {
                name: '한국어',
                image: koreaIcon,
            },
            category: {
                name: '소설',
                image: novelIcon,
            },
            medium: {
                name: '이북',
                image: ebookIcon,
            },
        },
    };

    ChartJS.register(
        LinearScale,
        CategoryScale,
        BarElement,
        PointElement,
        LineElement,
        Legend,
        Tooltip
    );

    return (
        <Container>
            <div className='row'>
                <div className='col-lg-12 col-xl-6'>
                    <HorizontalBookCard book={dummy.lastBook} />
                </div>

                <div className='col-lg-12 col-xl-6'>
                    <Card>
                        <Card.Body>
                            <h3>이번달 통계</h3>

                            <Chart
                                type='line'
                                data={{
                                    labels: [
                                        'January',
                                        'February',
                                        'March',
                                        'April',
                                        'May',
                                        'June',
                                    ],
                                    datasets: [
                                        {
                                            datasets: [
                                                {
                                                    label: 'My First dataset',
                                                    backgroundColor:
                                                        'rgb(255, 99, 132)',
                                                    borderColor:
                                                        'rgb(255, 99, 132)',
                                                    data: [
                                                        0, 10, 5, 2, 20, 30, 45,
                                                    ],
                                                },
                                            ],
                                        },
                                    ],
                                }}
                            />
                        </Card.Body>
                    </Card>
                </div>
            </div>
        </Container>
    );
};

export default Main;
