import React from 'react';
import { Chart } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    LinearScale,
    CategoryScale,
    BarElement,
    PointElement,
    LineElement,
    Legend,
    Tooltip,
} from 'chart.js';

const ReadTimeChart = ({ length, date = new Date(), timeData }) => {
    ChartJS.register(
        LinearScale,
        CategoryScale,
        BarElement,
        PointElement,
        LineElement,
        Legend,
        Tooltip
    );

    return (
        <Chart
            type='line'
            data={{
                labels: [
                    ...Array.from({ length: length }, (_, i) => {
                        const day =
                            (i + 1 + date.getDate() - length / 2 + 30) % 30;
                        return day + '일';
                    }),
                ],
                datasets: [
                    {
                        label: '',
                        backgroundColor: 'rgb(255, 99, 132)',
                        borderColor: 'rgb(255, 99, 132)',
                        data: [
                            ...Array.from(
                                { length: length },
                                (_, i) => Math.random() * 250
                            ),
                        ],
                    },
                ],
            }}
        />
    );
};

export default ReadTimeChart;
