import React, { useEffect } from 'react';
import { Chart } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    LinearScale,
    CategoryScale,
    BarElement,
    PointElement,
    LineElement,
    Legend,
    Tooltip,
} from 'chart.js';

const ReadTimeChart = ({
    endDate = new Date(),
    startDate = new Date(endDate).setDate(endDate.getDate - 14),
    timeData,
}) => {
    ChartJS.register(
        LinearScale,
        CategoryScale,
        BarElement,
        PointElement,
        LineElement,
        Legend,
        Tooltip
    );

    const startEndDateDifference = Math.ceil(
        Math.abs(endDate - startDate) / (1000 * 60 * 60 * 24)
    );

    useEffect(() => {
        console.log(endDate - startDate);
        console.log(Math.abs(endDate - startDate));
        console.log();
        console.log();
        console.log(
            Math.ceil(Math.abs(endDate - startDate) / (1000 * 60 * 60 * 24))
        );
    }, []);

    return (
        <Chart
            type='line'
            data={{
                labels: [
                    ...Array.from(
                        { length: startEndDateDifference },
                        (_, i) => {
                            const day = new Date(startDate).setDate(
                                startDate.getDate() + i
                            );
                            return day.getDate() + '일';
                        }
                    ),
                ],

                datasets: [
                    {
                        label: '',
                        backgroundColor: 'rgb(255, 99, 132)',
                        borderColor: 'rgb(255, 99, 132)',
                        data: [
                            ...Array.from(
                                { length: startEndDateDifference },
                                (_, i) => Math.random() * 250
                            ),
                        ],
                    },
                ],
            }}
        />
    );
};

export default ReadTimeChart;
