import React from 'react';
import { Card, Container } from 'react-bootstrap';
import koreaIcon from '../images/language/korea.png';
import novelIcon from '../images/category/novel.png';
import ebookIcon from '../images/medium/ebook.png';
// Components
import HorizontalBookCard from '../common/HorizontalBookCard';
import ReadTimeChart from '../statistics/ReadTimeChart';

const Main = () => {
    const dummy = {
        lastBook: {
            title: '위대한 게츠비',
            author: 'F. 스콧 피츠제럴드',
            image: 'http://minumsa.minumsa.com/wp-content/uploads/bookcover/075_위대한개츠비-300x511.jpg',
            startDate: '',
            currentPage: 100,
            endPage: 400,
            language: {
                name: '한국어',
                image: koreaIcon,
            },
            category: {
                name: '소설',
                image: novelIcon,
            },
            medium: {
                name: '이북',
                image: ebookIcon,
            },
        },
    };

    return (
        <Container>
            <div className='row row-eq-height'>
                <div className='col-lg-12 col-xl-6 mb-4'>
                    <HorizontalBookCard book={dummy.lastBook} />
                </div>

                <div className='col-lg-12 col-xl-6 mb-4'>
                    <Card className='h-100'>
                        <Card.Body>
                            <h3>이번달 통계</h3>

                            <ReadTimeChart
                                startDate={new Date().setDate(new Date() - 14)}
                                endDate={new Date()}
                            />
                        </Card.Body>
                    </Card>
                </div>
            </div>
        </Container>
    );
};

export default Main;
