import React from 'react';
import { Chart } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    LinearScale,
    CategoryScale,
    BarElement,
    PointElement,
    LineElement,
    Legend,
    Tooltip,
} from 'chart.js';

const ReadTimeChart = ({
    endDate = new Date(),
    startDate = new Date(endDate).setDate(new Date(endDate).getDate - 14),
    data,
}) => {
    ChartJS.register(LinearScale, CategoryScale, PointElement, LineElement);

    return (
        <Chart
            type='line'
            data={{
                labels: [
                    ...Array.from(
                        {
                            length: 14,
                        },
                        (_, i) => {
                            let day = new Date(startDate).setDate(
                                new Date(startDate).getDate() + i
                            );
                            return new Date(day).getDate() + '일';
                        }
                    ),
                ],

                datasets: [
                    {
                        label: '',
                        backgroundColor: 'rgb(255, 99, 132)',
                        borderColor: 'rgb(255, 99, 132)',
                        data: data,
                    },
                ],
            }}
        />
    );
};

export default ReadTimeChart;
